import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

// User Profile Schema
export const userProfileSchema = z.object({
  id: z.string(),
  name: z.string().min(1),
  photo: z.string().optional(),
  createdAt: z.string(),
});

// Bible Verse Schema
export const bibleVerseSchema = z.object({
  id: z.string(),
  book: z.string(),
  chapter: z.number(),
  verse: z.number(),
  text: z.string(),
  reference: z.string(),
  category: z.enum(['comfort', 'hope', 'gratitude', 'peace', 'strength']).optional(),
});

// Devotional Schema
export const devotionalSchema = z.object({
  id: z.string(),
  title: z.string(),
  content: z.string(),
  verse: z.string(),
  verseReference: z.string(),
  date: z.string(),
});

// Note Schema
export const noteSchema = z.object({
  id: z.string(),
  title: z.string(),
  content: z.string(),
  type: z.enum(['devotional', 'prayer', 'verses', 'reflection']),
  createdAt: z.string(),
  updatedAt: z.string(),
});

// Hymn Schema
export const hymnSchema = z.object({
  id: z.string(),
  number: z.number(),
  title: z.string(),
  lyrics: z.array(z.string()),
  chorus: z.string().optional(),
});

// Quiz Response Schema
export const quizResponseSchema = z.object({
  id: z.string(),
  emotion: z.enum(['alegre', 'preocupado', 'triste', 'esperancoso', 'grato']),
  timestamp: z.string(),
  responseVerse: z.string(),
});

// App Settings Schema
export const appSettingsSchema = z.object({
  darkMode: z.boolean(),
  notificationsEnabled: z.boolean(),
  morningTime: z.string(),
  eveningTime: z.string(),
  fontSize: z.enum(['small', 'medium', 'large']),
  prayerAlarmEnabled: z.boolean().optional(),
  morningPrayerEnabled: z.boolean().optional(),
  eveningPrayerEnabled: z.boolean().optional(),
  morningPrayerTime: z.string().optional(),
  eveningPrayerTime: z.string().optional(),
  prayerAlarmSound: z.string().optional(),
});

export type UserProfile = z.infer<typeof userProfileSchema>;
export type BibleVerse = z.infer<typeof bibleVerseSchema>;
export type Devotional = z.infer<typeof devotionalSchema>;
export type Note = z.infer<typeof noteSchema>;
export type Hymn = z.infer<typeof hymnSchema>;
export type QuizResponse = z.infer<typeof quizResponseSchema>;
export type AppSettings = z.infer<typeof appSettingsSchema>;

