        {/* Today's Events */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Clock className="w-5 h-5 mr-2 text-primary" />
              Eventos de Hoje
            </CardTitle>
          </CardHeader>
          <CardContent>
            {events.filter(e => e.date === new Date().toISOString().split('T')[0]).length === 0 ? (
              <p className="text-muted-foreground text-center py-4">
